mapper.select:
select id, region, contractNo, contractVersion, subscriptionDate, signer, signingMode, signingDate, buildingInfo, buildingSize, originalPrice, totalPrice, signTotalPrice, leasebackPrice, backPremium, payStartDate, contractTerDate, paybackDate, beneficiary, proposalId, contractStatus, tariff, taxAmount, logs from finance_contract where 1 = 1
and signingDate >= #{filterStartTime,typeHandler=TimestampLongHandler}
and signingDate < #{filterEndTime,typeHandler=TimestampLongHandler}
and contractVersion = #{contractVersion}
and buildingSize >= #{buildingStartSize}
and buildingSize < #{buildingEndSize}
and signingMode = #{signingMode,javaType=SigningMode,typeHandler=SigningModeHandler}
and contractStatus = #{status,javaType=ContractStatus,typeHandler=ContractStatusHandler}

mapper.update:
update finance_contract




                when id=#{item.id} then #{item.contractStatus,javaType=ContractStatus,typeHandler=ContractStatusHandler}


                when id=#{item.id} then finance_contract.contractStatus




where id in

    #{item.id,typeHandler=LongTypeHandler}
